import axios from "axios";

const API_BASE = process.env.REACT_APP_API_URL;

export const getExpenses = async () => {
  try {
    console.log("Calling API:", `${API_BASE}/getExpenses`);
    const res = await axios.get(`${API_BASE}/getExpenses`);
    return res.data;
  } catch (err) {
    console.error("Failed to get expenses:", err.response || err.message);
    throw err;
  }
};

export const addExpense = async (expense) => {
  const res = await axios.post(`${API_BASE}/addExpense`, expense);
  return res.data;
};

export const deleteExpense = async (id) => {
  const res = await axios.delete(`${API_BASE}/deleteExpense/${id}`);
  return res.data;
};
